import { Button, FormControl, FormControlLabel, Radio, RadioGroup } from '@material-ui/core'
import React, { useState } from 'react'
import { useDispatch, useSelector } from "react-redux"
import './ShippingScreen.css'
import { savePaymentMethod } from '../actions/cartActions'
import CheckoutSteps from "../components/CheckoutSteps"



const PaymentScreen = ({ history }) => {
    const cart = useSelector(state => state.cart)
    const { shippingAddress } = cart

    if (!shippingAddress) {
        history.push("/shipping")
    }

    const [paymentMethod, setPaymentMethod] = useState('Paypal')


    const dispatch = useDispatch()

    const submitHandler = (e) => {
        e.preventDefault()
        dispatch(savePaymentMethod(paymentMethod))
        history.push('/placeorder')
    }

    return (
        <div className="shipping__form">
            <CheckoutSteps step1 step2 step3 />
            <h1>Payment Method</h1>
            <FormControl className="form__container" onSubmit={submitHandler}>
                <RadioGroup row aria-label="position" name="position" defaultValue="top">
                    <FormControlLabel
                        control={<Radio color="primary" />}
                        label="Paypal or Credit Card"
                        id="PayPal"
                        name="paymentMethod"
                        value="PayPal"
                        onChange={e => setPaymentMethod(e.target.value)}
                    />
                    <FormControlLabel
                        control={<Radio color="primary" />}
                        label="Transfer Bank Mandiri Syariah"
                        id="MandiriSyariah"
                        name="paymentMethod"
                        value="MandiriSyariah"
                        onChange={e => setPaymentMethod(e.target.value)}
                    />

                    <Button variant="contained" type="submit" color="secondary" style={{ width: "100px" }}>
                        Continue
                </Button>
                </RadioGroup>
            </FormControl>
        </div>
    )
}

export default PaymentScreen
