    }

    return (
        <FormContainer>
            <ValidatorForm onSubmit={submitHandler}>
                <h2>Sign In</h2>
                <TextValidator
                    label="Emaail"
                    onChange={(e) => setEmail(e.target.value)}
                    name="email"
                    value={email}
                    validators={['required', 'isEmail']}
                    errorMessages={['this field is required', 'email is not valid']}
                ></TextValidator>

            </ValidatorForm>
        </FormContainer>
    )
}

export default LoginScreen
