            <div className="product__info">
                <h3>{product.name}</h3>
                <Rating value={product.rating} text={`${product.numReviews} reviews`} />
                <h4>{`Price: $${product.price}`}</h4>
                <p>{product.description}</p>
            </div>
        </div>











