import React, { useState, useEffect } from 'react'
import "./ProductScreen.css"
import { Button } from '@material-ui/core'
import Rating from '../components/Rating'

const ProductScreen = ({ match }) => {
    const [product, setProduct] = useState({})

    useEffect(() => {

        const fetchProduct = async () => {
            const { data } = await axios.get(`/api/products/${match.params.id}`)

            setProduct(data)
        }

        fetchProduct()
    }, [])

    return (
        <div className="product__item">
            <div className="product__image">
                <img src={product.image} alt={product.name}></img>
            </div>

            <div className="product__left">
                <h3>{product.name}</h3>
                <Rating value={product.rating} text={`${product.numReviews} reviews`} />
                <h4>{`Price: $${product.price}`}</h4>
                <p>{product.description}</p>
            </div>

            <div className="product__right" role="table">
                <div className="flex__table row" role="rowgroup">
                    <div className="flex__row" role="cell">Price:</div>
                    <div className="flex__rowChild" role="cell">${product.price}</div>
                </div>

                <div className="flex__table row" role="rowgroup">
                    <div className="flex__row" role="cell">Status:</div>
                    <div className="flex__rowChild" role="cell">{product.countInStock > 0 ? "In Stock" : "Out of Stock"}</div>
                </div>

                <div className="add__cart" role="rowgroup">
                    <Button variant="outlined" disabled={product.countInStock === 0}>Add to cart</Button>
                </div>

            </div>
        </div>











        // <div className="product">
