import { Button, TextField } from '@material-ui/core'
import React, { useState } from 'react'
import { useDispatch, useSelector } from "react-redux"
import './ShippingScreen.css'
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';

const ShippingScreen = ({ history }) => {
    const [address, setAddress] = useState('')
    const [city, setCity] = useState('')
    const [postalCode, setPostalCode] = useState('')
    const [country, setCountry] = useState('')

    const submitHandler = (e) => {
        e.preventDefault()
        console.log('submit')
    }

    return (
        <div className="shipping__form">
            <h1>Shipping</h1>
            <ValidatorForm className="form__container" onSubmit={submitHandler}>
                <TextField className="shipping__input" style={{ width: "195px" }}
                    label="Address"
                    onChange={(e) => setAddress(e.target.value)}
                    name="address"
                    required
                    multiline
                    rows={4}
                    value={address}>
                </TextField>
                <TextValidator className="shipping__input"
                    label="City"
                    onChange={(e) => setCity(e.target.value)}
                    name="city"
                    required
                    value={city}>
                </TextValidator>
                <TextValidator className="shipping__input"
                    label="Postal Code"
                    onChange={(e) => setPostalCode(e.target.value)}
                    name="postalCode"
                    required
                    value={postalCode}>
                </TextValidator>
                <TextValidator className="shipping__input"
                    label="Country"
                    onChange={(e) => setCountry(e.target.value)}
                    name="country"
                    required
                    value={country}>
                </TextValidator>
                <Button variant="contained" color="secondary" style={{ width: "100px" }}>
                    Continue
</Button>
            </ValidatorForm>
        </div>
    )
}

export default ShippingScreen
