import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { Box, Button, Grid } from '@material-ui/core'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import CheckCircleRoundedIcon from '@material-ui/icons/CheckCircleRounded';
import CancelRoundedIcon from '@material-ui/icons/CancelRounded';
import BorderColorRoundedIcon from '@material-ui/icons/BorderColorRounded';
import DeleteForeverRoundedIcon from '@material-ui/icons/DeleteForeverRounded';
import { listUsers } from '../actions/userActions'

const UserListScreen = () => {
    const dispatch = useDispatch()

    const userList = useSelector(state => state.userList)
    const { loading, error, users } = userList

    useEffect(() =>{
        dispatch(listUsers())
    }, [dispatch])

    return (
        <div className="userList__container">
            <h1>Users</h1>
            {loading ? <Loader /> : error ? <Message severity="error">{error}</Message>
            : (
                <TableContainer component={Paper}>
      <Table className="userList__table" aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Id</TableCell>
            <TableCell align="right">Name</TableCell>
            <TableCell align="right">Email</TableCell>
            <TableCell align="right">Admin</TableCell>
            <TableCell align="right"></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {users.map((user) => (
            <TableRow key={user._id}>
              <TableCell component="th" scope="row">
                {user._id}
              </TableCell>
              <TableCell align="right">{user.name}</TableCell>
              <TableCell align="right"><a href={`mailto:${user.email}`} >{user.email}</a></TableCell>
              <TableCell align="right">{user.isAdmin ? (<CheckCircleRoundedIcon />) : (
                  <CancelRoundedIcon />
              ) }</TableCell>
              <TableCell align="right">
                  <Link to={`/user/${user._id}/edit`}>
                    <Button variant="contained" size="small"><BorderColorRoundedIcon /></Button>
                  </Link></TableCell>
                  <Button variant="contained" color="secondary" onClick={() => deleteHandler(user._id)}><DeleteForeverRoundedIcon /></Button>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
            )}
        </div>
    )
}

export default UserListScreen
